$.jqtab=function(a,b,c){$(b).find("li").hide();$(a).find("li:first").addClass("thistab").show();$(b).find("li:first").show();$(a).find("li").bind(c,function(){$(this).addClass("thistab").siblings("li").removeClass("thistab");var d=$(a).find("li").index(this);$(b).children().eq(d).show().siblings().hide();return false})};
